INSERT INTO `educationalResourceMap` VALUES ('5ba763e57e4e7316d93842e2', '北京市对外贸易学校', 1, 'zyjyxx', '北京市朝阳区来广营水岸南街1号院', '100012', 116.44820, 40.05974, '84923159', NULL, NULL, NULL, NULL, '中专 ', 83185, 147, 78, NULL, NULL, 16, 2490, '秉承“以仁爱包容之心助每个孩子成长”的理念，以培养“懂做人之道、有立世之能”的准职业人为目标，立足行业、瞄准市场，特色化办学。所开专业紧贴社会需求，从内外贸领域辐射到城市轨道交通和民航运输领域。学校管理规范，教学、生活设施齐备，交通便利，环境优雅，文化氛围浓郁，是一所利于身心健康发展的花园式教学单位。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e57e4e7316d93842e4', '北京经济技术职业学院', 1, 'zyjyxx', '河北省廊坊市三河市燕郊经济开发区北京经济技术职业学院', '065202', 116.84404, 40.02008, '61598651', NULL, NULL, NULL, NULL, '高等职业学院 ', 223569, 140, 79, NULL, NULL, 22, 1285, '学院是北京市民政局5A级社会组织单位，是北京市“3+2”中高职衔接改革试点项目单位。坚持“产教融合”发展战略，坚持“校企合作”模式。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e57e4e7316d93842e6', '北京商贸学校', 1, 'zyjyxx', '北京市大兴区西红门路14号', '100162', 116.36676, 39.79752, '60221741', NULL, NULL, NULL, NULL, '普通中专 ', 67687, 167, 63, NULL, NULL, 14, 1300, '学校秉承“以教学为中心、为培养目标和经济建设服务、面向企业、面向社会、面向受教育者不同需要”的办学思想，立足首都经济发展新定位和京津冀一体化协同发展战略新需求，立足二商集团打造产业发展新格局的需要，坚持走“内涵发展，特色发展，共同发展，国际发展”之路，在专业建设、德育教育、中高职衔接等方面打造新亮点、办出新特色。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e57e4e7316d93842e8', '北京交通职业技术学院', 1, 'zyjyxx', '北京市昌平区西关环岛西边1公里', '102200', 116.21085, 40.23502, '办公室89703145，招就处89707874', NULL, NULL, NULL, NULL, '高等职业学院 ', 200000, 233, 93, NULL, NULL, 22, 1711, '学院秉承“办精品高职、创就业名牌”办学理念，已形成“交通土建类”、“汽车机电类”、“城市服务类”、“轨道交通类”四大特色专业群，突出重点特色专业优势，大力推进“3+2”中高职教育衔接工作。积极开展全方位、深层次、重实效的校企合作，先后与北京现代、中软国际、华润置地、地铁公司等多家知名企业建立合作关系，形成“订单式”人才培养模式，打造了特色专业群，形成了自己的办学优势。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e57e4e7316d93842ea', '北京铁路电气化学校', 1, 'zyjyxx', '北京市昌平区南口镇文化路1号', '102202', 116.12579, 40.24868, '60797798-8007/8027', NULL, NULL, NULL, NULL, '普通中专 ', 140582, 213, 147, NULL, NULL, 23, 2939, '建于1952年，隶属北京市教委，是国家重点校、国家中职改革发展示范校、北京市首批高端技术技能人才贯通培养试验校。获得“全国职业教育先进单位”、“黄炎培职业教育优秀学校”等荣誉。设轨道交通技术、供用电技术、电气技术类23个专业，培养4万多名毕业生，与铁路、城市轨道交通、供用电等150多家企业长期合作，与52家企业深度合作实施订单培养，就业率始终保持在98%以上。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e57e4e7316d93842ec', '北京市平谷区职业学校', 1, 'zyjyxx', '北京市平谷区峪口镇峪西大街50号', '101205', 117.00896, 40.19486, '69909471', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 49950, 153, 145, NULL, NULL, 8, 306, '学校始终坚持“为学生创造机会，为教师搭建平台，为社会培养人才”的办学理念，围绕“修一技之长，养优秀品行”的校训，不断深化升、产、教融合的“三赢”模式，为学生开启多元化成长之路。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e57e4e7316d93842ee', '北京市延庆区第一职业学校', 1, 'zyjyxx', '北京市延庆区延庆镇湖南东路8号', '102100', 116.00306, 40.46102, '69185797', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中）', 113700, 229, 171, NULL, NULL, 19, 715, '学校遵循“以服务为宗旨、以就业为导向、以质量为核心”的办学宗旨，提出了“着力培养学生良好的职业道德，精心培养学生过硬的专业技能，注重培养学生一定的创新思维和创新能力”的工作主题。坚持“先教学生做人，再教学生做事”的教育宗旨。促进学生德智体美全面发展，努力把学校办成学生成才、家长满意、社会欢迎的优质中等职业学校。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e57e4e7316d93842f0', '北京培黎职业学院', 1, 'zyjyxx', '北京市海淀区双清路1号', '100085', 116.36005, 39.89745, '62956117', NULL, NULL, NULL, NULL, '高等职业学院 ', 490946, 211, 97, NULL, NULL, 30, 1972, '学院传承发扬老校长艾黎和何克倡导的职业教育思想，以“手脑并用，创造分析”为校训，培养有知识、有技能，会动脑、会动手，善分析、勇创造，德智体美劳全面发展的人才。推行没有爱就没有教育，没有兴趣就没有学习，学生成长在活动中，教书育人在细微处的教育理念，建立了产政教融合的育人机制，为每位学生提供适合其兴趣、爱好、特长的教育，帮助学生实现人生梦想。', NULL);
