INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d93842f6', '北京市大兴区第一职业学校', 1, 'zyjyxx', '北京市大兴区黄村镇永华路2号', '102612', 116.28847, 39.72796, '61201064', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 184667, 191, 129, NULL, NULL, 11, 871, '学校以创建创新型学校为先导，坚持“德育为首，全面育人，内造素质，外塑形象”的办学方针，遵循“专业企业有机接轨、中职高职相互融通、国内国外联合办学”的职教理念，贯彻“以上岗就业为导向、以企业需求为标准、以职业能力为本位”的教学原则，走出了一条“质量立校、品牌强校、特色名校”的办学之路。学校设有飞机维修、动漫游戏等信息及航空两大类11个专业，致力于为区域发展培养高端应用型人才。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d93842f8', '首都医科大学附属卫生学校', 1, 'zyjyxx', '北京市丰台区花乡张家路口107号', '100070', 116.33088, 39.85012, '83609591', NULL, NULL, NULL, NULL, '普通中专 ', 45336, 105, 83, NULL, NULL, 4, 967, '北京市重点中专学校，第一批参加北京市高端技术技能人才贯通培养试验项目的学校，护理专业是北京市中等职业学校示范专业。秉承“扶伤济世、敬德修业”的校训，培养具有良好职业道德和行为规范，掌握必备的文化基础、专业知识和专业技能，在各级各类医疗卫生和社区卫生服务机构等从事临床护理、预防保健、助产、康复治疗等工作的高素质技术技能人才。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d93842fa', '北京信息职业技术学院', 1, 'zyjyxx', '北京市朝阳区芳园西路5号', '100015', 116.48919, 39.97895, '64312725', NULL, NULL, NULL, NULL, '高等职业学院 ', 202066, 859, 377, NULL, NULL, 42, 5681, '学校以促进首都社会发展和学生全面成长为己任，提供高水平的职业教育（培训）与技术服务，持续提高教育教学质量，最大限度满足社会和学生的需求与期望。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d93842fc', '北京市园林学校', 1, 'zyjyxx', '北京市房山区良乡镇广阳西路9号', '102488', 116.16226, 39.76407, '61359886', NULL, NULL, NULL, NULL, '普通中专 ', 74954, 91, 60, NULL, NULL, 10, 275, '是一所行业办学特色鲜明的国家级中等职业改革发展示范学校，校训为“修德强技，树木树人”。学校坚持学历教育与职业培训并重的发展格局，职业培训围绕公园系统、园林绿化行业职工的四新教育需求开展技术培训、素质培养。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d93842fe', '北京劳动保障职业学院', 1, 'zyjyxx', '北京市朝阳区惠新东街5号', '100029', 116.42968, 39.99189, '64941661', NULL, NULL, NULL, NULL, '高等职业学院 ', 24648, 236, 268, NULL, NULL, 17, 3899, '学院同时开展继续教育、技能培训与鉴定、中外合作教育等类型教育服务，是全国唯一一所同时拥有国家级高技能人才培训基地和国家级专业技术人员继续教育基地的高等职业院校。学院逐步形成“一体两翼、三足鼎立”的发展格局，即以高等职业教育为主体，学历教育和社会培训为“两翼”；人力社保、城市运行与安全和现代服务三大重点专业群“三足鼎立”，并重发展。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384300', '北京市实验职业学校', 1, 'zyjyxx', '北京市西城区菜园街13号', '100053', 116.36009, 39.88479, '63461004', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 13477, 173, 99, NULL, NULL, 4, 253, '北京市实验职业学校创始于1980年，是一所多专业、宽内涵、集学历教育与职后培训并举、办学成果显著的国家级重点中等职业学校。\n学校秉承“紧贴市场，全面育人”的办学理念，以“办学为社会服务，教育为学生服务，追求师生群体和个体的最优发展”为办学思路，深化教学改革，巩固发展内涵，发挥骨干专业优势，突出培养职业能力，努力为首都经济建设做出更大的贡献。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384302', '北京劳动保障职业学院（南校区）', 1, 'zyjyxx', '北京市朝阳区惠新东街5号', '100029', 116.42968, 39.99189, '64941661', NULL, NULL, NULL, NULL, '高等职业学院 ', 24648, 236, 268, NULL, NULL, 17, 3899, '学院同时开展继续教育、技能培训与鉴定、中外合作教育等类型教育服务，是全国唯一一所同时拥有国家级高技能人才培训基地和国家级专业技术人员继续教育基地的高等职业院校。学院逐步形成“一体两翼、三足鼎立”的发展格局，即以高等职业教育为主体，学历教育和社会培训为“两翼”；人力社保、城市运行与安全和现代服务三大重点专业群“三足鼎立”，并重发展。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384304', '北京劳动保障职业学院（北校区）', 1, 'zyjyxx', '北京市昌平区旧县村1号', '102200', 116.19168, 40.23154, '80114009', NULL, NULL, NULL, NULL, '高等职业学院 ', 190686, 236, 268, NULL, NULL, 17, 3899, '学院同时开展继续教育、技能培训与鉴定、中外合作教育等类型教育服务，是全国唯一一所同时拥有国家级高技能人才培训基地和国家级专业技术人员继续教育基地的高等职业院校。学院逐步形成“一体两翼、三足鼎立”的发展格局，即以高等职业教育为主体，学历教育和社会培训为“两翼”；人力社保、城市运行与安全和现代服务三大重点专业群“三足鼎立”，并重发展。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384306', '北京汇佳职业学院', 1, 'zyjyxx', '北京市昌平区中关村科技园区昌平园创新路20号', '102200', 116.24751, 40.19858, '010-51631166  ', NULL, NULL, NULL, NULL, '高等职业学院 ', 245000, 167, 101, NULL, NULL, 10, 1826, '学院坚持新型、高品位、国际化办学理念和以质量求生存、以特色求发展、以诚信求未来的办学方针，以德育为根本，培育和践行社会主义核心价值观，培养学生良好职业道德；以就业为导向，坚持校企结合、工学结合、学做结合,培养学生良好职业技能；学习和践行IB国际先进教育理念，开展探究式教学，培养具有国际视野的适应经济社会发展需求的高素质高技能人才。\r\n', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384308', '北京市供销学校', 1, 'zyjyxx', '北京市房山区兴东大街277号', '102400', 116.04544, 39.71764, '89345104', NULL, NULL, NULL, NULL, '普通中专 ', 42000, 91, 54, NULL, NULL, 10, 733, '学校以“凝心聚力、协同发展”为指导，深化体制机制改革，全面深化校企合作，开发一体化课程；积极探索多元化办学体制，通过ISO9000质量管理体系认证，完善管理流程。围绕职业意识、职业习惯、职业道德、职业能力形成了“每月一个主题、每月一个活动”的教育管理模式,以一体化教学改革为抓手，打造新型职教科研队伍。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938430a', '北京市商业学校', 1, 'zyjyxx', '昌平区北七家镇曺八西路28号', '102209', 116.43299, 40.14785, '81761419', NULL, NULL, NULL, NULL, '普通中专 ', 209301, 313, 201, NULL, NULL, 23, 3757, '学校是国家级重点中等职业学校，是全国职业教育先进集体、全国教育系统先进单位。充分发挥国企办学优势，立足首都城市功能定位，以培养现代高端服务业人才为己任，重点服务京津冀地区商业、金融、信息、交通、物流、教育等领域。学校以中专学历教育为主，开设会计、电子商务和学前教育等23个专业，目前已构建了集中专、“3+2”中高衔接、“3+2+2”贯通培养、成考、自考、远程教育、培训鉴定为一体的多层次人才培养体系。', NULL);
