INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938430e', '北京市昌平职业学校', 1, 'zyjyxx', '北京市昌平区沙河镇松兰堡', '102206', 116.30105, 40.15401, '69732142', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 104811, 293, 266, NULL, NULL, 39, 2872, '学校是首批国家中等职业教育改革发展示范学校、国家级重点职业学校、北京市现代化标志性中等职业学校，先后被评为“全国职业教育先进单位”、“全国中等职业教育德育工作先进集体”、“全国模范职工之家”。学校坚持“师生为本，科学经营学校”的办学理念，以“把需要工作的人培养成工作需要的人”为使命，实施“六有六成六桥”德育工程，打造“有用、有趣、有效”的“三有”的课堂，不断提高人才培养质量。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384310', '北京市昌平职业学校南口校区', 1, 'zyjyxx', '北京市昌平区南口镇温南路', '102206', 116.12535, 40.20990, '89793500', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 63936, 17, 16, NULL, NULL, 2, 154, '学校是首批国家中等职业教育改革发展示范学校、国家级重点职业学校、北京市现代化标志性中等职业学校，先后被评为“全国职业教育先进单位”、“全国中等职业教育德育工作先进集体”、“全国模范职工之家”。学校坚持“师生为本，科学经营学校”的办学理念，以“把需要工作的人培养成工作需要的人”为使命，实施“六有六成六桥”德育工程，打造“有用、有趣、有效”的“三有”的课堂，不断提高人才培养质量。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384312', '北京市什刹海体育运动学校（100中）', 1, 'zyjyxx', '北京市西城区地安门西大街57号', '100009', 116.39678, 39.93996, '83222375', NULL, NULL, NULL, NULL, '普通中专 ', 33696, 327, 93, NULL, NULL, 1, 181, '北京市什刹海体育运动学校是北京市体育局下属的体育运动训练基地，是以培养体育专业人才和为国家输送竞技体育后备人才为宗旨的中等职业学校，被国家教育部和国家体育总局分别评定为“国家级重点中等职业学校”和“全国高水平体育后备人才基地”。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384314', '北京财贸职业学院校本部', 1, 'zyjyxx', '北京市通州区北关大街88号', '101101', 116.65433, 39.92678, '89532019', NULL, NULL, NULL, NULL, '高等职业学院 ', 148061, 639, 362, NULL, NULL, 25, 5300, '北京财贸职业学院是市属公办普通高等学校和国家示范性高等职业院校，是首都文明单位、北京高校平安校园示范校、全国毕业生就业典型经验高校、全国创新创业典型经验高校。学校坚持以立德树人为根本，以服务发展为宗旨，以就业为导向，充分利用地处北京城市副中心、京冀多校区办学的区位优势，主动适应首都经济社会发展的需求，为首都现代服务业发展提供人才支撑和智力服务。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384316', '北京卫生职业学院一院区', 1, 'zyjyxx', '北京市房山区窦店镇下坡店村大窦路12号', '102402', 116.07292, 39.69431, '63209001', NULL, NULL, NULL, NULL, '高等职业学院 ', 12182, 291, 114, NULL, NULL, 8, 2335, '学院坚持“以人为本、德育为先、质量为重”的办学宗旨，以培养“专业型、实用型、国际型”高等医药卫生技术人才为方向。一院区是学院主办公区，包括药学系、相关医学与管理系、医学技术系，设有药学、药剂、医学检验技术、医学影像技术、口腔医学技术、卫生信息管理等多个专业。药学专业被教育部等多个部门授予全国首批健康服务类示范专业、市级示范专业。医学检验技术、医学影像技术、口腔医学技术为学院特色专业。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384318', '北京卫生职业学院二院区', 1, 'zyjyxx', '北京市通州区玉带河西街24号', '101149', 116.65591, 39.90723, '69548394', NULL, NULL, NULL, NULL, '高等职业学院 ', 51444, 158, 71, NULL, NULL, 2, 1680, '二院区以护理系为主，设有护理、助产2个专业。其中护理专业是学院精心打造的重点专业，也是中央财政重点支持的专业之一。护理类专业的培养目标是培养具有一定科学文化素养，德智体美全面发展，具有良好职业素质、人际交往与沟通能力，熟练掌握护理操作技能，能在各级各类医疗卫生和社区卫生服务机构从事护理、助产、保健、康复、健康教育等多项工作，具有职业生涯发展基础的技能型、服务型的高素质劳动者为主。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938431a', '北京卫生职业学院三院区', 1, 'zyjyxx', '北京市通州区九棵树东路128号', '101101', 116.67493, 39.89059, '60527431', NULL, NULL, NULL, NULL, '高等职业学院 ', 20627, 154, 75, NULL, NULL, 4, 1155, '三院区以中药与康复系为主，设有中药学、康复治疗技术、中医康复技术等3个专业。中药学专业是学院重点专业之一，康复治疗技术专业是学院特色专业。建有中药标本馆、生命科学馆、中药鉴定调剂、中药炮制制剂、模拟药店、药用植物、中药化学、制药设备、人体解剖、推拿、针灸、作业治疗、物理因子治疗、运动治疗等实验实训室。注重加强专业建设，在教育教学过程中强化学生职业能力和综合素质培养，形成具有自身特色的人才培养模式。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938431c', '北京市燕山职业学校', 1, 'zyjyxx', '北京市房山区燕山东万路2号', '102502', 115.97551, 39.76922, '69341165', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 15000, 39, 29, NULL, NULL, 4, 120, '走“职普融通”之路，培养学生的职业素养、职业技能和生存技能，为学生一生发展奠基。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938431e', '北京市海淀区卫生学校', 1, 'zyjyxx', '北京市海淀区清河永泰庄北路27号', '100192', 116.35801, 40.04245, '62911802', NULL, NULL, NULL, NULL, '普通中专 ', 21867, 100, 62, NULL, NULL, 5, 1311, '于1960年建校，是公立全日制中等职业学校，有26所二级以上教学实习合作医院，是海淀区委党校卫生工委分校；对卫生专业技术人员提供学历教育、技术培训、技能提升、讲座、论坛等多元化的终身教育平台；同时面向社会和中小学生开展职业体验、社会实践和科普研学课程；学校具有养老护理员和护工培训资质；与加拿大、德国、澳大利亚和台湾地区等相关大学签有“直通车”协议，开辟了卫生专业国际交流和职业国际化发展的渠道。', NULL);
