INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384322', '北京电子科技职业学院', 1, 'zyjyxx', '北京经济技术开发区凉水河一街9号', '100176', 116.51289, 39.77563, '87220669', NULL, NULL, NULL, NULL, '高等职业学院 ', 538000, 927, 534, NULL, NULL, 44, 6860, '办学理念：立足开发区，服务首都，融入京津冀，依托高精尖产业，为经济社会发展提供人才服务。\r\n主要特色：学校围绕职业教育的发展与改革，实施“产教融合机制化、人才供给精准化、社会服务多样化”的“三化”战略，形成了独具特色的“产学协同”培养高素质技术技能人才的新模式、新方法，全面提升了人才培养质量和服务区域经济社会发展的能力。\r\n培养目标：培养具有创新实践能力的高端技术技能型人才和应用型人才。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384324', '北京国际职业教育学校\n（北京站校区）', 1, 'zyjyxx', '北京市东城区\n北京站东街柳罐胡同2号', '100005', 116.43935, 39.91243, '65598391', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 12000, 96, 57, NULL, NULL, 2, 45, '与北京城市学院、北京汇佳职业学院进行中高职衔接，培养具备扎实的文化基础知识、良好的职业道德、学前教育专业理论知识与技能、具有国际化视野可持续发展的高素质技能型人才。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384326', '北京国际职业教育学校\n（鼓楼校区）', 1, 'zyjyxx', '北京市东城区\n宝钞胡同21号', '100009', 116.40584, 39.95217, '64041174', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 13445, 170, 83, NULL, NULL, 5, 308, '学生培养目标为“文化基础扎实、传承传统文化、蕴含中华底蕴、具备国际视野”，全面发展的实用型人才。', NULL);
