INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938432a', '北京市第一七九中学', 1, 'zyjyxx', '北京市东城区左安浦园小区4号楼', '100061', 116.44463, 39.87997, '67125384', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 11241, 76, 43, NULL, NULL, 5, 18, '我们按照“开放、灵活，适应市场，求实、创新，办出特色”的办学指导思想和“以人为本，以德立校，学有所长，服务社会”的办学宗旨，走“高端、精品、特色”之路，发挥好首都核心功能区职业教育的作用。立足为首都的经济建设做贡献，以服务为宗旨，以就业为导向，以能力为本位，强化专业对社会的适应性和针对性，并逐步实现与国际双向接轨、与行业接轨，满足学生就业与升学的不同发展需求。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938432c', '北京现代职业学校', 1, 'zyjyxx', '东城区定安里12号', '100075', 116.41626, 39.86843, '67014116', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 1, 98, 53, NULL, NULL, 4, 38, '以“特色鲜明、差异发展、深入挖掘”为目标，为社会服务。教师根据专业优势、实训基地资源及各职业文化氛围，针对不同年龄生源特点，设计、开发一系列与行业岗位高度对接的职业体验及综合实践课程。采用“基地授课”“送课下校”“团队订制”3种组织实施方式，全面开启职业体验新模式。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938432e', '北京市朝阳区职业技术学校', 1, 'zyjyxx', '北京市朝阳区广渠门外广和里八巷2号', '100022', 116.45261, 39.89545, '67783813', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 14565, 46, 35, NULL, NULL, 2, 90, '属公办职业高中，现设有电气运行与控制和建筑装饰两个专业，学校本着让每一个孩子健康发展的办学理念，努力践行“静待花开，顺势造型”的教育使命，办学理念正在向学生的内心需求和精神追求深入；办学模式正在向家庭安宁社会和谐的深度和广度拓展，培养模式正在向培养学生的兴趣、特长改革；教育模式正在向纠偏的多种途径展开探索；教师队伍建设正在向“四职”的研究渗透；学校管理正在向精细实机制落实。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384330', '北京市外事学校', 1, 'zyjyxx', '北京市西城区西直门内永祥胡同3号', '100035', 116.36419, 39.94086, '66169540', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 25384, 147, 119, NULL, NULL, 6, 169, '学校建于1980年，是首批国家级重点中等职业高中，全国教育系统先进集体，全国职业教育先进单位、全国职业指导先进单位、北京2008年奥运会、残奥会奥林匹克教育工作做突出贡献单位，全国职业院校旅游类示范专业点、全国校企合作示范基地。学校坚持服务政治，服务中央，服务行业企业的高端定位，以“爱国、敬业、勤学、健美”为校训，实践热情大气，追求卓越的外事精神，培养两万余优秀技术技能型旅游服务人才。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384332', '北京市门头沟中等职业学校', 1, 'zyjyxx', '北京市门头沟区城子东街4号', '102300', 116.10586, 39.95832, '69853494', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 22275, 129, 87, NULL, NULL, 8, 305, '门头沟区中等职业学校是北京市属全日制公办职业高中，北京市首批办好的职业高中校之一。学校坚持“容融相映，创造指尖上的艺术”的办学理念，以聚力、情怀、容融、绿色为办学方针，采取“三并举，六融合”的办学策略，立足区域发展，以社会和市场需求为驱动，以就业为导向，以培训和体验为两翼，促进学生职业生涯可持续发展，为区域社会可持续发展服务，立志打造学历教育、培训、体验三位一体的京西绿色职业教育基地。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384334', '北京市房山区第二职业高中（东校区）', 1, 'zyjyxx', '北京市房山区窦店镇望楚村', '102433', 116.08764, 39.66253, '89373151转8015', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 33350, 68, 51, NULL, NULL, 1, 380, '学校经过多年的探索与实践形成“以服务为宗旨、以就业为导向，面向社会、面向市场办学，进一步增强办学生机与活力；坚持走内涵发展道路，不断深化教育教学改革，提升育人质量，大力推行工学结合、校企合作、顶岗实习”的人才培养模式。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384336', '北京市房山区第二职业高中（西校区）', 1, 'zyjyxx', '北京市房山区窦店镇下坡店村', '102402', 116.07294, 39.69707, '81303558', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 46690, 85, 72, NULL, NULL, 4, 832, '学校经过多年的探索与实践形成“以服务为宗旨、以就业为导向，面向社会、面向市场办学，进一步增强办学生机与活力；坚持走内涵发展道路，不断深化教育教学改革，提升育人质量，大力推行工学结合、校企合作、顶岗实习”的人才培养模式。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384338', '北京金隅科技学校', 1, 'zyjyxx', '北京市房山区琉璃河车站东街22号', '102403', 116.05555, 39.59944, '69320011', NULL, NULL, NULL, NULL, '普通中专 ', 112921, 291, 165, NULL, NULL, 17, 2074, '本校是一所具有60多年历史的国家级重点中等专业学校。学校１９８０年被教育部确定为全国重点中等专业学校,１９９１年被国家教育委员会、国家计划发展委员会、劳动部、财政部授予全国职业技术先进单位，２００２年被北京市教委、北京市劳动和社会保障局等单位授予北京市职业教育先进单位，2003年12月被北京市教委评为现代化标志学校。2011年经教育部审批，成为首批国家中等职业教育改革发展示范校建设项目校。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938433a', '北京市昌平卫生学校', 1, 'zyjyxx', '北京市昌平区东环路138号', '102200', 116.24690, 40.22306, '69746597', NULL, NULL, NULL, NULL, '普通中专 ', 24339, 93, 38, NULL, NULL, 8, 744, '学校贯彻执行国家教育、卫生工作方针，坚持以服务为宗旨、以就业为导向的原则，培养具有一定科学文化素养，德智体美全面发展，具有良好职业素质、人际交往与沟通能力，熟练掌握各项专业操作技能，能在各级各类医疗卫生、社区卫生服务和养老机构从事护理、社区护理、预防保健、临床检验、康复技术和中药、药剂、老年服务与管理等工作，具有职业生涯发展基础的技能型、服务型的高素质劳动者。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938433c', '北京戏曲艺术职业学院', 1, 'zyjyxx', '北京市丰台区马家堡东里8号', '100068', 116.38360, 39.86187, '67571886', NULL, NULL, NULL, NULL, '高等职业学院 ', 25346, 346, 172, NULL, NULL, 6, 407, '坚持服务首都战略定位需要，以服务需求为导向，以继承发扬民族文化艺术、传承保护非物质文化遗产为使命，以专业建设为核心，以提高人才培养质量为目标，贯彻“树德为首、育才为要、传承为根、创新为本、因材施教、突出实践”的办学理念，深化校企合作、产教融合的办学模式，培养具有扎实的专业基本理论、基本知识和基本技能，具有交流沟通、团队协作、创新和就业能力，具有国际视野，具有良好职业道德，符合社会需求的艺术人才。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938433e', '北京青年政治学院', 1, 'zyjyxx', '北京市朝阳区花家地街9号', '100102', 116.48116, 39.98677, '84778227', NULL, NULL, NULL, NULL, '高等职业学院 ', 62196, 328, 233, NULL, NULL, 27, 3467, '北京青年政治学院是北京市市级示范性高职院校，其前身为1956年成立的北京市团校。1986年7月，改革开放的总设计师邓小平同志亲笔题写院名。在六十多年的建设发展中，时任北京市委副书记金鉴、汪家镠、李志坚同志先后担任学院院务指导委员会主任。新时期新阶段，学院强化政治、青年和人文特色，努力把学院建设成为全国一流高等职业教育院校和全国一流团青教育培训与研究基地，建设成为开放型、有特色、高水平的青年人大学。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384340', '北京市丰台区职业教育中心学校芳古园校区', 1, 'zyjyxx', '北京市丰台区方庄芳古园二区9号', '100078', 116.42633, 39.87597, '67628098', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 12000, 82, 70, NULL, NULL, 3, 320, '本校区立足首都功能定位，以服务区域经济发展为使命，培养适合餐饮服务业发展需要的德、智、体、美全面发展，具有良好的职业道德和行为规范，掌握餐饮服务业所需要的高素质技能型人才。烹饪专业是北京市骨干特色专业、北京市“以工作过程为导向”新课程实施实验专业，2012年被认定为北京市示范专业。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384342', '北京市丰台区职业教育中心学校芳群园校区', 1, 'zyjyxx', '北京市丰台区芳群园四区18号', '100078', 116.42643, 39.87587, '67627252', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 9329, 17, 16, NULL, NULL, 1, 152, '本校区为汽车后市场培养“高质量、复合型、实用型”的技能型人才。汽车运用与维修专业是国家级改革发展示范校重点建设专业、北京市中职示范专业，是北京市中等职业学校以工作过程为导向课程改革项目试点专业。2013年被列入国家中等职业教育改革发展示范校建设的重点专业建设项目。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384344', '北京市丰台区职业教育中心学校三营门校区', 1, 'zyjyxx', '北京市丰台区南苑路115号', '100086', 116.40868, 39.81310, '67995816', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 8206, 53, 48, NULL, NULL, 1, 240, '立足首都四个中心建设，是学前教育行业重要人才供给基地。专业师资力量雄厚，36年办学历史，办学规模持续高位。园校一体化运行机制，与北京市34家幼儿园协同育人，每年大量优秀毕业生有效缓解行业人才缺口，提升首都学前教育行业质量，保障区域民生经济发展。打造“丰职教育服务品牌”，服务于市民学习中心、中小学开放性实践体验课程、学习型城市建设。附属幼儿园有效解决区域学龄前儿童入园难问题。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384346', '北京市丰台区职业教育中心学校木樨园校区', 1, 'zyjyxx', '北京市丰台区南木樨园6号', '100075', 116.41124, 39.86433, '67212084', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 9625, 60, 58, NULL, NULL, 1, 330, '立足首都四个中心建设，是学前教育行业重要人才供给基地。专业师资力量雄厚，36年办学历史，办学规模持续高位。园校一体化运行机制，与北京市34家幼儿园协同育人，每年大量优秀毕业生有效缓解行业人才缺口，提升首都学前教育行业质量，保障区域民生经济发展。打造“丰职教育服务品牌”，服务于市民学习中心、中小学开放性实践体验课程、学习型城市建设。附属幼儿园有效解决区域学龄前儿童入园难问题。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384348', '北京市丰台区职业教育中心学校洋桥校区', 1, 'zyjyxx', '北京市丰台区西罗园四区29号', '100077', 116.39508, 39.86739, '87252562', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 17365, 74, 66, NULL, NULL, 4, 948, '以打造信息核技术专业技能人才为建设品牌。校区有电子商务专业、物联网技术应用专业、航空服务专业、计算机网络技术专业。注重专业建设以课改带动专业发展，以大赛为抓手促学生技能发展。以教学项目为抓手促教师专业化发展。校区注重校园文化建设，以一校一品建设为主线，以培育感动丰职人物、建设厚德精工学风为抓手，近年来校区各专业在全国中等职业学校技术技能比赛中均取得了优异成绩。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938434a', '北京市丰台区职业教育中心学校贾家花园校区', 1, 'zyjyxx', '北京市丰台区贾家花园15号院', '100079', 116.42135, 39.85985, '87829560', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 9300, 23, 21, NULL, NULL, 2, 166, '校区以影视全流程人才培养为基准，适度拓展技能培养技术方向。打造全流程人才培养教学体系，通过引入行业资源、行业大师、行业认证，打造基于高端影视人才培养孵化的平台体系，提升专业群的影响力以及区别同类院校的核心竞争力，并满足示范窗口的功能需求。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938434c', '北京市丰台区职业教育中心学校云岗校区', 1, 'zyjyxx', '北京市丰台区云岗田城东里2号', '100074', 116.15708, 39.82437, '68743542', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 21280, 26, 20, NULL, NULL, 3, 298, '开设老年服务与管理专业和老年大学课程，服务丰台区域需求。依据校区特色积极为中小学服务。开展周边中小学生社会实践活动如开展绿植养护、微景观制作、芽苗菜种植的特色体验活动。积极与云岗社区开展社区居民体验活动，开展了插花培训礼仪培训衍纸画烫画葫芦等多种多样1500多人的培训活动。与云岗街道妇联和计生办开展的失独家庭的心灵家园的共建活动和养老护理培训活动。积极为周边企业服务。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938434e', '北京市自动化工程学校', 1, 'zyjyxx', '北京市朝阳区科荟路甲2号', '100101', 116.37653, 40.01543, '64871046', NULL, NULL, NULL, NULL, '普通中专 ', 36000, 128, 84, NULL, NULL, 16, 1365, '学校办学宗旨:以人为本，服务社会，成就未来，全面培养学生综合职业能力和素质；办学目标：创建有特色、精品型、国际化的优质特色职业学校；学校办学定位：以社会需求为导向，立足首都，面向全国；服务于现代制造业、信息技术产业、交通行业发展，服务于首都经济社会发展；学历教育与社会培训并重，为现代化一流的企业、行业培养高素质技能型人才，使受教育者具备基本职业技能和可持续发展的就业、创业的能力。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384350', '北京京北职业技术学院', 1, 'zyjyxx', '北京市怀柔区小中富乐一区188号', '101400', 116.64646, 40.34568, '89681399', NULL, NULL, NULL, NULL, '高等职业学院 ', 124000, 226, 141, NULL, NULL, 15, 2169, '北京京北职业技术学院是公办独立设置的全日制高等职业技术院校，是北京市示范性高职学院。它的前身是1984年成立的北京建筑工程学院怀柔分院，2002年更名为北京京北职业技术学院。学院坐落在美誉的怀柔城区。学院占地186亩，建筑面积62159平方米；现有全日制在校生2100人。形成了“紧贴经济发展办学，按市场需求育人”的办学特色。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384352', '北京体育职业学院', 1, 'zyjyxx', '北京市丰台区光彩北路4号', '100075', 116.41752, 39.85989, '87280847', NULL, NULL, NULL, NULL, '高等职业学院 ', 85660, 143, 67, NULL, NULL, 4, 670, '学院秉承“崇德尚体，授技育人”的校训，以服务首都“四个中心”定位为根本宗旨，以培养北京优秀运动员学生为根本任务，以提高运动员学生综合文化素质和体育职业技能为根本目标，始终坚持训教一体化模式联合培养竞技体育人才和体育产业从业人员。学院现设置四个专业，其中面向社会招生的专业分别是社会体育、体育运营与管理、体育保健与康复专业，运动训练专业仅面向市级优秀运动队进行招生，学制分别为五年和两年。', NULL);
