INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384356', '北京水利水电学校', 1, 'zyjyxx', '北京市朝阳区定福庄东里1号', '100024', 116.57252, 39.92182, '65762428', NULL, NULL, NULL, NULL, '普通中专 ', 38528, 147, 81, NULL, NULL, 15, 838, '学校是北京市唯一一所公办全日制水务类中等职业学校，已有60余年办学历史，现为国家级重点中等职业学校、全国水利中等职业教育示范学校、首都文明单位、北京市职业教育先进单位。\n学校秉承“修德、勤奋、求实、兴水”的校训精神，坚持以立德树人为根本，以服务发展为宗旨，以促进就业为导向，不断深化教育教学改革，强化内涵建设，不断提升技能人才培养质量。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384358', '北京市怀柔区职业学校', 1, 'zyjyxx', '北京市怀柔区雁栖开发区雁栖大街7号', '101407', 116.70595, 40.36826, '61669697', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 230000, 227, 178, NULL, NULL, 8, 1290, '是以培养二、三产专业技术人才为主、兼顾一产的综合性国家级重点职业学校。汽修、机加工、旅服会展专业是北京市示范专业，培养了大批专业技能人才，有力地支持了当地经济社会的发展，同时圆了众多职校学生大学梦想。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938435a', '北京政法职业学院', 1, 'zyjyxx', '北京市大兴区黄亦路（团河段）2号', '102628', 116.37558, 39.76859, '89269900', NULL, NULL, NULL, NULL, '高等职业学院 ', 320000, 358, 236, NULL, NULL, 22, 3962, '学院突出“立德明法、重能强技”人才培养特色，以立德树人为根本任务，以特色、内涵、质量建设为主线，深入开展教育教学改革，先后完成了人才培养工作评估、市级示范校和国家骨干校申报、示范校建设及职业教育分级制改革试点等重点任务，并以优秀等级通过市级示范性建设验收，学院发展连上三个台阶，综合办学实力大幅度提升，办学优势特色不断彰显。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938435c', '北京工业职业技术学院', 1, 'zyjyxx', '北京市石景山区石门路368号', '100042', 116.12901, 39.96453, '51511004', NULL, NULL, NULL, NULL, '高等职业学院 ', 240120, 498, 350, NULL, NULL, 33, 5238, '北京工业职业技术学院是一所以工科专业为主、独立设置的公办普通高等职业学院。学校前身为创建于1956年的北京煤炭工业学校.学校在快速发展的过程中，坚持走“高端化、精品化、国际化”的发展道路。2010年7月，学校以优秀等级通过教育部、财政部组织的“国家示范性高等职业院校项目建设”验收。2015年成为首批试点北京高端技术技能人才贯通培养项目的三所高职院校之一。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938435e', '北京市财会学校', 1, 'zyjyxx', '北京市西城区西便门内大街69号', '100053', 116.35925, 39.90217, '010-83150497', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 9934, 102, 57, NULL, NULL, 3, 156, '学校秉承“以就业为导向，以服务为宗旨，以能力为本位，以质量为核心”的办学思想。恪守“明德、励能、承业、益民”的培养目标，探索将行为习惯、职业习惯和专业发展有机结合的“过程育人”方式。逐步形成以围绕课程建设为中心的学生职业能力和实践为中心的学生终身发展两种能力的培养模式。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384360', '北京科技职业学院', 1, 'zyjyxx', '北京市昌平区沙河镇沙阳路18号', '102206', 116.25844, 40.12476, '58911018', NULL, NULL, NULL, NULL, '高等职业学院 ', 246000, 274, 106, NULL, NULL, 21, 1294, '北京科技职业学院是由以周继庭同志为首的北科昊月集团秉持“育人报国”的深切情怀于1997年创办。学院创办至今，得到了党和国家领导人、北京市政府等相关部门、教育界等社会各界的帮助和支持。截至目前，我院占地面积两千余亩，教育教学用房总建筑面积70余万平方米，教学仪器设备总值约0.8亿元。经过20年的发展，我院已成为一所服务区域发展、具有专业特色、培养“精专”人才的民办高等职业院校。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384362', '北京科技职业学院（八达岭校区）', 1, 'zyjyxx', '北京市延庆县祥元东路1号', '102199', 116.02662, 40.42576, '58915568', NULL, NULL, NULL, NULL, '高等职业学院 ', 884667, 0, 0, NULL, NULL, 0, 0, '国际教育、国际论坛、特色小镇等', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384364', '北京社会管理职业学院燕郊校区', 1, 'zyjyxx', '北京东燕郊经济开发区燕灵路2号', '101601', 116.80437, 39.96033, '61595435', NULL, NULL, NULL, NULL, '高等职业学院 ', 204800, 335, 171, NULL, NULL, 16, 4035, '北京社会管理职业学院是民政部直属的全日制公办高等职业院校，集民政部培训中心、民政部职业技能鉴定指导中心、全国民政行业教育教学指导委员会秘书处、中国社会工作学会秘书处于一体，按照“立足民政、面向社会、服务全国、适应市场、开放办学”的原则开设儿童、老年、康复、殡葬、社工、公共管理类的16个专业及方向，基本覆盖了基层社区、特殊行业和特殊人群等服务领域，是民政人才培养的重要基地和民政行业重要智库。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384366', '北京社会管理职业学院大兴校区（在建）', 1, 'zyjyxx', '北京市大兴区观音寺街道北京政法职业学院南侧', '102628', 116.38217, 39.76161, '61595435', NULL, NULL, NULL, NULL, '高等职业学院 ', 269900, 335, 171, NULL, NULL, 16, 4035, '北京社会管理职业学院是民政部直属的全日制公办高等职业院校，集民政部培训中心、民政部职业技能鉴定指导中心、全国民政行业教育教学指导委员会秘书处、中国社会工作学会秘书处于一体，按照“立足民政、面向社会、服务全国、适应市场、开放办学”的原则开设儿童、老年、康复、殡葬、社工、公共管理类的16个专业及方向，基本覆盖了基层社区、特殊行业和特殊人群等服务领域，是民政人才培养的重要基地和民政行业重要智库。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384368', '北京市密云区职业学校', 1, 'zyjyxx', '北京市密云区果园西路2号', '101500', 116.83485, 40.39340, '010-69057468', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 61000, 204, 182, NULL, NULL, 13, 854, '与多家企业建立校企合作，就业率98%以上；单考单招升学率98%以上；省市、国家级重点中等职业学校；北京市现代化标志性学校；国家中等职业教育改革发展示范校。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938436a', '北京市密云区职业学校统军庄校区', 1, 'zyjyxx', '北京市密云区统军庄中心街甲471号', '101500', 116.75103, 40.35378, '010-69057468', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 43000, 204, 182, NULL, NULL, 13, 854, '密云区职业学校旅游服务、烹饪实训基地，配套设施齐全。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938436c', '北京市密云区职业学校塘子校区', 1, 'zyjyxx', '北京市密云区巨各庄镇豆各庄村', '101500', 117.01073, 40.36750, '010-69057468', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 32000, 204, 182, NULL, NULL, 13, 854, '密云区职业学校数控、汽修专业实训基地，实训设备千余套，设备总值达亿元。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938436e', '北京市实美职业学校', 1, 'zyjyxx', '北京市西城区百万庄大街19号', '100037', 116.35408, 39.93423, '68347677', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 7868, 348, 299, NULL, NULL, 5, 506, '国家级重点职业学校，全国职业教育先进单位，办有学前教育、美术绘画、形象设计、数字媒体、法律专业。学校坚持“全面育人，以优取胜”的办学方针和“服务社会，注重实践”的办学宗旨，形成“职普融通”多元化办学格局，坚持赏识教育理念，开展有趣有用有效课堂教学改革，开展普通高考、艺术高考，中高职3+2对接培养。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384370', '北京经贸职业学院', 1, 'zyjyxx', '北京房山区良乡长虹东路高教园区', '102488', 116.17070, 39.72696, '81355799/13718249517', NULL, NULL, NULL, NULL, '高等职业学院 ', 105693, 176, 72, NULL, NULL, 15, 1330, '学校以创新为引领，以优质服务为宗旨，以高质量就业为导向，秉持“人人都能成才”的教育理念，以培养学生社会责任感、创新精神和实践能力为重点，确立了“以教学为中心，以育人为根本，一切为了学生健康成长成才”的办学理念，“依法治校、质量立校、人才强校、特色兴校”的学校发展理念和“自强砺志、尚德树人”的学校核心文化，形成了“广泛开展志愿服务，培育奉献社会精神”的办学特色，逐步形成“一主多元”的办学格局。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384372', '北京市杂技学校（北京市国际艺术学校）', 1, 'zyjyxx', '北京经济技术开发区建安街9号', '100176', 116.54089, 39.78305, '67803699', NULL, NULL, NULL, NULL, '普通中专 ', 52000, 144, 109, NULL, NULL, 5, 804, '学校以培养中专学历人才，促进杂技艺术事业发展，为高等艺术院校输送高质量的生源，为专业艺术院团、企事业单位输送实用的专业人才为培养目标。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384374', '北京农业职业学院', 1, 'zyjyxx', '北京市房山区长阳镇稻田南里5号', '102442', 116.23158, 39.77587, '80358899', NULL, NULL, NULL, NULL, '高等职业学院 ', 742921, 525, 271, NULL, NULL, 21, 2961, '北京农业职业学院是全日制公立普通高等学校，隶属于北京市农村工作委员会。学院溯源于1958年建校的北京市农业学校，现为国家示范性高等职业院校、北京市职业教育先进单位。学院坚持“立足首都，面向全国，服务‘三农’”的办学宗旨，秉承“立德、修业、求知、笃行”的院训精神，以立德树人为根本，以产教深度融合、校企紧密合作为导向，为经济社会发展提供人才支撑、科研支持与社会服务。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384376', '北京农业职业学院（北校区）', 1, 'zyjyxx', '北京市海淀区香山普安店29号', '100093', 116.23185, 39.99833, '82595008', NULL, NULL, NULL, NULL, '高等职业学院 ', 32919, 145, 61, NULL, NULL, 15, 604, '北京农业职业学院北校区为学院文科专业及中外合作专业办学校区，同时为中共北京市委农工委党校办学地。校区位于香山脚下，环境优美，设施精良。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d9384378', '北京农业职业学院（北苑校区）', 1, 'zyjyxx', '北京市朝阳区北苑路46号院', '100012', 116.42463, 40.03802, '84921501', NULL, NULL, NULL, NULL, '高等职业学院 ', 23435, 71, 28, NULL, NULL, 2, 242, '北京农业职业学院北苑校区（国际教育学院）位于亚北居住区中心，毗邻奥林匹克公园，是学院贯通培养项目（前两年）及留学生教育所在校区；该校区教学设施现代、生活设施完备、文体设施齐全，有稳定的外籍教师长期任教，有完备的培养方案、课程计划、教学模式。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938437a', '北京农业职业学院（清河校区）', 1, 'zyjyxx', '北京市昌平区回龙观镇二拨子2号', '102208', 116.31617, 40.08523, '52781223', NULL, NULL, NULL, NULL, '高等职业学院 ', 46241, 97, 50, NULL, NULL, 5, 248, '北京农业职业学院清河校区（机电工程学院）始建于1960年，前身为北京市八一农业机械化学校，位于回龙观文化区，毗邻中关村科技园，2004年12月并入北京农业职业学院。57年来，机电工程学院凭借新颖的办学特色和独特的育人模式，培养了19000余名毕业生，服务首都特别是京郊的发展。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938437c', '北京市房山区房山职业学校（南校区）', 1, 'zyjyxx', '北京市房山区兴房大街52号', '102400', 115.98829, 39.69159, '69323304', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 22082, 78, 70, NULL, NULL, 4, 782, '坚持“11335”办学思想：即充分发挥职教资源优势，培训高素质技能型人才，服务区域经济和社会发展；育人为本，服务为先，厚德精技，和谐发展；三大板块：中高职学历教育、中小学素质教育、职成一体化培训。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba763e67e4e7316d938437e', '北京市房山区房山职业学校（实训培训基地）', 1, 'zyjyxx', '北京市房山区河北镇河南村', '102417', 115.94371, 39.82335, '60377264', NULL, NULL, NULL, NULL, '职业高中（含设有成人中专的职业高中） ', 29215, 20, 13, NULL, NULL, 7, 921, '开展业余成人中专班学历教育；社区教育；居民（农民）中短期培训；中小学生社会大课堂综合实践活动；中职在校生实习实训。', NULL);
