INSERT INTO `educationalResourceMap` VALUES ('5ba764127e4e7316d93843ea', '中国人民公安大学（木樨地校区）', 1, 'gdyx', '木樨地南里1号', '100038', 116.34442, 39.90801, '83903019', NULL, NULL, NULL, NULL, '国务院委办属院校', 184000, 1561, 644, NULL, NULL, 13, 13000, '学校以培养“忠诚可靠、专业精通、文武兼备、作风顽强”的卓越公安人才为目标，不断优化人才培养方案，探索和建立课堂教学与警务实践有机结合、突出实践能力培养的“教、学、练、战”一体化的人才培养模式。', NULL);
