INSERT INTO `educationalResourceMap` VALUES ('5ba764127e4e7316d9384416', '中国地质大学（北京）', 1, 'gdyx', '北京市海淀区学院路29号', '100083', 116.35965, 39.99589, '82322005', NULL, NULL, NULL, NULL, '教育部直属院校 ', 525843, 1443, 1004, NULL, NULL, 41, 86079, '学校认真贯彻党的教育方针，坚持社会主义办学方向，秉承\"艰苦朴素、求真务实\"的优良校训，坚持\"特色加精品\"的办学理念，培养\"品德优良、基础厚实、知识广博、专业精深\"的高素质创新人才，加快建设高水平研究型大学，为实现\"地球科学领域世界一流大学\"的长远目标而不懈奋斗！', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba764127e4e7316d9384418', '北京语言大学', 1, 'gdyx', '北京市海淀区学院路15号', '100083', 116.35494, 39.99909, '010-82303033', NULL, NULL, NULL, NULL, '教育部直属院校 ', 328694, 1199, 679, NULL, NULL, 29, 13826, '北京语言大学是中国唯一一所以对来华留学生进行汉语、中华文化教育为主要任务的国际型大学，素有“小联合国”之称。同时是一所以语言教学与研究为特色和优势，中文、外语及相关学科协同发展的多科性大学，是我国中外语言、文化研究的学术重镇和培养涉外高级人才的摇篮。目前与世界上58个国家和地区的321所大学及教育机构建立了合作交流关系，形成了全方位、多领域、深层次、富有成效的国际教育格局。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba764127e4e7316d938441a', '中国社会科学院大学（中国社会科学院研究生院）', 1, 'gdyx', '北京市房山区长于大街11号', '102488', 116.19022, 39.74547, '81360214', NULL, NULL, NULL, NULL, '国务院委办属院校 ', 408400, 754, 264, NULL, NULL, 14, 4368, '中国社会科学院大学本科教育将继承和发扬“师徒制”的传统，本科生学业导师由来自中国社会科学院和国内外的知名专家学者担任；学校将在一些特定学科中，陆续开展连续培养，进行“本-硕-博”一体化培养；中国社会科学院国际交流合作遍及世界100多个国家和地区的科学院、知名大学、智库、政府部门及国际组织等，学校注重通过国际学术交流、联合培养的方式，培养学生的国际视野及对外交流能力，同时中国社科大也将大力引进国际学术资源，为学生提供国际化的多元教育。兼任学生导师的中国社会科学院专家学者，承担大量的国家级哲学社会科学规划重点研究项目或重大理论问题和实际问题研究任务。', NULL);
