INSERT INTO `educationalResourceMap` VALUES ('5ba764127e4e7316d9384420', '首都经济贸易大学校本部', 1, 'gdyx', '北京市丰台区花乡张家路口121号', '100070', 116.32322, 39.85025, '83952828', NULL, NULL, NULL, NULL, '市属高校 ', 294352, 1490, 843, NULL, NULL, 43, 16784, '学校坚持“立足北京、服务首都、面向全国、走向世界”，秉承“崇德尚能，经世济民”的校训，以培养适应当代经济和社会发展需要、德智体全面发展、理论基础扎实、知识面较宽、富有创新精神和实践能力的高素质应用型人才为目标，朝着建设“现代化、国际化、多科性、有特色的国内一流、国际知名财经大学”的目标开拓奋进。', NULL);
