INSERT INTO `educationalResourceMap` VALUES ('5ba764137e4e7316d9384478', '中国戏曲学院', 1, 'gdyx', '北京市丰台区万泉寺400号', '100073', 116.33930, 39.86841, '63353774', NULL, NULL, NULL, NULL, '市属高校 ', 86246, 422, 271, NULL, NULL, 14, 2591, '中国戏曲学院是中国戏曲教育的最高学府和戏曲艺术家的摇篮。学校的定位是高水平特色艺术大学，办学目标是将学校建成全国戏曲高级人才培养中心、戏曲理论研究中心、戏曲文化信息交流与传播中心，成为民族特色鲜明、国内水准一流、国际影响广泛的戏曲艺术大学。学校坚持特色鲜明的教学、科研、创作、实践“四位一体”的人才培养模式，围绕戏曲艺术的传承、传播与发展开设专业和开展教学。学生的培养目标是“培养和造就全面发展、基础扎实、实践能力强、具有传统文化底蕴和创新精神的戏曲高级专门人才”。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba764137e4e7316d938447a', '北京理工大学（中关村校区）', 1, 'gdyx', '北京市海淀区中关村南大街5号', '100081', 116.32895, 39.96551, '68914247', NULL, NULL, NULL, NULL, '国务院委办属院校 ', 711152, 3279, 2152, NULL, NULL, 67, 26276, '北京理工大学创立于1940年，前身是延安自然科学院，是中国共产党创办的第一所理工科大学，首批进入国家“211工程”和“985工程”及国家“双一流”建设行列，学校现隶属于工业和信息化部。学校以“高远的理想、精深的学术、强健的体魄、恬美的心境”为人才培养目标，坚持瞄准国家重大战略需求和世界科技发展前沿锐意进取，秉承“延安根、军工魂”的红色基因。“德以明理、学以精工”的校训，“团结、勤奋、求实、创新”的校风，以及徐特立老院长倡导的“实事求是、不自以为是”的学风，共同铸就了学校独特的精神气质和文化内核。', NULL);
