INSERT INTO `educationalResourceMap` VALUES ('5ba764137e4e7316d938448a', '北京城市学院(顺义校区)', 1, 'gdyx', '北京市顺义区杨镇木燕路', '101399', 116.82171, 40.16225, '69449311', NULL, NULL, NULL, NULL, '市属高校 ', 666700, 2200, 900, NULL, NULL, 40, 16000, '北京城市学院创立于1984年，是经国家教育部批准成立的一所全日制综合性普通高校，以本科教育为主，并举办研究生、高职专科教育，建有附属中小学、幼儿园。学校坚持以市场为导向，紧密围绕北京城市未来发展之必须，设置有百余个社会热门、行业紧缺、产业急需的学科专业，并以应用型、实用性、国际化为特色培养人才。学校专职教师队伍素质高、能力强，学生管理严格，校风校园文化氛围良好。学校办学的高质量赢得了毕业生就业的高质量，毕业生以社会适应能力强、专业动手能力强、学习发展能力强和下得去、用得上、干得好而著称，连续二十年就业率在全国高校中名列前茅。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba764137e4e7316d938448c', '北京城市学院（航天城校区)', 1, 'gdyx', '北京市海淀区西北旺镇皇后店中路6号', '100094', 116.25995, 40.09687, '62479495', NULL, NULL, NULL, NULL, '市属高校 ', 173342, 2200, 900, NULL, NULL, 20, 8000, '北京城市学院创立于1984年，是经国家教育部批准成立的一所全日制综合性普通高校，以本科教育为主，并举办研究生、高职专科教育，建有附属中小学、幼儿园。学校坚持以市场为导向，紧密围绕北京城市未来发展之必须，设置有百余个社会热门、行业紧缺、产业急需的学科专业，并以应用型、实用性、国际化为特色培养人才。学校专职教师队伍素质高、能力强，学生管理严格，校风校园文化氛围良好。学校办学的高质量赢得了毕业生就业的高质量，毕业生以社会适应能力强、专业动手能力强、学习发展能力强和下得去、用得上、干得好而著称，连续二十年就业率在全国高校中名列前茅。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba764137e4e7316d938448e', '中国人民大学', 1, 'gdyx', '北京市海淀区中关村大街59号', '100872', 116.32622, 39.97557, '62511083', NULL, NULL, NULL, NULL, '教育部直属院校 ', 639580, 3399, 1881, NULL, NULL, 81, 28165, '中国人民大学是中国共产党创办的第一所新型正规大学，是以人文社会科学为主的综合性研究型全国重点大学，直属于教育部，由教育部与北京市共建。学校是国家首批“985工程”“211工程”重点建设大学，2017年首批入选国家“世界一流大学和一流学科”建设名单。学校在我国人文社会科学领域独树一帜。在第四轮学科评估中，学校有9个学科获评A+，分别是理论经济学、应用经济学、法学、社会学、新闻传播学、统计学、工商管理、公共管理和马克思主义理论，获评A+的人文社会学科数量为8个，排名全国第2。在ESI世界学科排名中，学校社会科学总论、经济学商学2个学科均已进入世界前1%，人文社会科学研究水平处于世界一流行列。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba764137e4e7316d9384490', '中国人民大学（中国人民大学继续教育学院）', 1, 'gdyx', '北京市海淀区清华东路甲7号', '100083', 116.37015, 40.00762, '53261718', NULL, NULL, NULL, NULL, '教育部直属院校 ', 8582, 3399, 1881, NULL, NULL, 15, 3410, '中国人民大学继续教育学院是学校组织开展学历继续教育和非学历教育培训的办学单位。学院拥有辉煌的历史，其前身为1951年刘少奇同志亲自批准成立的中国人民大学函授教育，60余年来一直是中国高校继续教育的排头兵，有多位新一届国家部委主要负责人都有在中国人民大学函授教育的学习经历。中国人民大学清华东路校区目前主要承担学历继续教育任务，包括成人夜大学和网络教育。学院始终坚持正确的教育方向和质量第一的办学原则，充分依托学校的学科优势、师资优势和服务优势，充分整合校内外的一流教育资源，开设了会计学、金融学、工商管理、市场营销和人力资源管理等优势专业，精准服务于成人学历继续教育和网络教育需求。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba764137e4e7316d9384492', '北京工商大学', 1, 'gdyx', '海淀区阜成路11号、33号', '100048', 116.32096, 39.93106, '68984661', NULL, NULL, NULL, NULL, '市属高校 ', 210729, 1451, 949, NULL, NULL, 51, 7956, '北京工商大学至今已有68年的办学历史，是北京市重点建设的多科性大学，目前正加快建设具有商科、轻工和食品特色的世界一流大学。', NULL);
