INSERT INTO `educationalResourceMap` VALUES ('5ba764137e4e7316d93844a2', '北京物资学院', 1, 'gdyx', '北京市通州区富河大街321号', '101149', 116.64546, 39.93429, '89534627', NULL, NULL, NULL, NULL, '市属高校 ', 397016, 696, 493, NULL, NULL, 25, 8498, '　北京物资学院是一所以物流和流通为特色，以经济学科为基础，以管理学科为主干，经、管、理、工、文、法等多学科协调发展的公办普通高等院校。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba764137e4e7316d93844a4', '北京建筑大学（大兴校区）', 1, 'gdyx', '北京市大兴区永源路15号', '100044', 116.29397, 39.75029, '68322234', NULL, NULL, NULL, NULL, '市属高校 ', 501319, 1038, 650, NULL, NULL, 35, 11842, '是一所北京市和住建部共建，具有鲜明建筑特色、以工为主的多科性大学，是“北京城市规划、建设、管理的人才培养基地和科技服务基地”、“北京应对气候变化研究和人才培养基地”和“国家建筑遗产保护研究和人才培养基地”，是北京地区唯一一所建筑类高等学校。人才培养目标为：以立德树人为根本任务，弘扬“实事求是、精益求精”校训精神，注重知识、能力、素质协调发展，培养具有社会责任感、实践能力、创新精神和国际视野的建设领域高级专业骨干和领军人才。', NULL);
INSERT INTO `educationalResourceMap` VALUES ('5ba764137e4e7316d93844a6', '首都医科大学', 1, 'gdyx', '北京市右安门外西头条10号', '100069', 116.35963, 39.87355, '63291983', NULL, NULL, NULL, NULL, '市属高校 ', 163454, 1290, 623, NULL, NULL, 18, 12735, '首都医科大学创建于1960年,是北京市重点高等院校，是北京市政府、国家卫生健康委、教育部共建院校。设有10所学院、21所临床医学院。学校培养医学及医学相关学科的学术型和应用型人才，有本科专业21个,三级学科博士点59个、硕士点78个,博士后科研流动站9个。有10个国家重点（培育）学科，拥有一批国家级和市级重点实验室和研究中心。学校秉承“扶伤济世、敬德修业”的校训，始终以立德树人为根本，遵循医学教育规律，牢记社会责任，把为健康中国健康北京建设、京津冀协同发展战略实施、国际一流和谐宜居之都建设做贡献为己任，努力办好人民满意的大学，培养人民满意的医生，做出能服务人民对健康美好需求的科研成果。', NULL);
